import Banner from '@/components/Banner.vue';
import Productos from '@/components/Productos.vue';
import { useStore } from 'vuex';
import { onMounted, computed } from 'vue';
import Filtros from '@/components/Filtros.vue';
export default {
  name: 'HomeView',
  components: {
    Productos,
    Banner,
    Filtros
