import { createStore } from 'vuex';
import { collection, getDocs } from 'firebase/firestore'; // Funciones de Firestore
import { db } from '../firebase.js'; // Importa la configuración de Firebase

export default createStore({
  state: {
    productos: [],
    carrito : {
      cantidadTotal: 0
    }
  },
  getters: {
    getProductos: (state) => state.productos
  },
  mutations: {
    setProductos(state, productos) {
      state.productos = productos;
    },
    AGREGAR_AL_CARRITO(state, producto) {
      const id = producto.id;

      // Verificar si el producto ya está en el carrito
      if (state.carrito[id]) {
        // Si ya está, incrementar la cantidad
        state.carrito[id].cantidad++;
      }}
  },
  actions: {
    async fetchProductos({ commit }) {
      try {
        // Obtener los cursos desde Firestore
        const productosSnapshot = await getDocs(collection(db, 'Productos'));
        const productos = productosSnapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
        commit('setProductos', productos);
        console.log(productos)
      } catch (error) {
        console.error("Error al obtener los productos:", error);
      }
    },
    agregarAlCarrito({ commit }, producto) {
      // Llamar a la mutación para agregar el producto al carrito
      commit('AGREGAR_AL_CARRITO', producto);
    },
  },
  modules: {
  }
})
